import { __decorate } from "tslib";
import { Component } from '@angular/core';
let FinancialnewsComponent = class FinancialnewsComponent {
    constructor(claimService, renderer) {
        this.claimService = claimService;
        this.renderer = renderer;
        this.defaultSymbol = "XAUUSD";
    }
    ngOnInit() {
        this.claimService.getTableData().subscribe(data => {
            this.tableData = data;
        });
        this.claimService.getTableData1().subscribe(data => {
            this.tableData1 = data;
        });
        this.claimService.getTableData7().subscribe(data => {
            this.tableData2 = data;
        });
    }
    ngAfterViewInit() {
        // Load with default symbol
        this.loadTradingViewWidget(this.defaultSymbol);
    }
    loadTradingViewWidget(symbol) {
        const script = document.createElement('script');
        script.type = 'text/javascript';
        script.src = 'https://s3.tradingview.com/tv.js';
        script.async = true;
        script.onload = () => this.createTradingViewWidget(symbol);
        document.head.appendChild(script);
    }
    createTradingViewWidget(symbol) {
        if (!this.tradingViewWidget) {
            // If the widget doesn't exist, create it
            this.tradingViewWidget = new window.TradingView.widget({
                "autosize": true,
                "symbol": symbol,
                "interval": "D",
                "timezone": "Etc/UTC",
                "theme": "light",
                "style": "1",
                "locale": "fr",
                "enable_publishing": true,
                "allow_symbol_change": true,
                "container_id": "tradingview_c976b"
            });
        }
        else {
            // If the widget exists, update the symbol
            this.tradingViewWidget.chart().setSymbol(symbol);
        }
    }
    onSymbolChange(event) {
        const selectedSymbol = event.target.value;
        // Remove the existing TradingView widget
        if (this.tradingViewWidget) {
            this.tradingViewWidget.remove();
            this.tradingViewWidget = null;
        }
        // Load a new TradingView widget with the selected symbol
        this.loadTradingViewWidget(selectedSymbol);
    }
    loadTradingViewScript() {
        const script = this.renderer.createElement('script');
        script.type = 'text/javascript';
        script.src = 'https://s3.tradingview.com/external-embedding/embed-widget-technical-analysis.js';
        script.async = true;
        // Add your widget configuration here
        script.text = `
      {
        "interval": "1m",
        "width": 425,
        "isTransparent": false,
        "height": 450,
        "symbol": "OANDA:XAUUSD",
        "showIntervalTabs": true,
        "displayMode": "single",
        "locale": "fr",
        "colorTheme": "light"
      }
    `;
        const container = document.querySelector('.tradingview-widget-container');
        this.renderer.appendChild(container, script);
    }
};
FinancialnewsComponent = __decorate([
    Component({
        selector: 'app-financialnews',
        templateUrl: './financialnews.component.html',
        styleUrls: ['./financialnews.component.css']
    })
], FinancialnewsComponent);
export { FinancialnewsComponent };
