import * as i0 from "@angular/core";
import * as i1 from "@angular/forms";
export class PortfolioComponent {
    constructor() {
        this.defaultSymbol = "XAUUSD";
    }
    ngOnInit() { }
    ngAfterViewInit() {
        // Load with the default symbol
        this.loadTradingViewWidget(this.defaultSymbol);
    }
    ngOnDestroy() {
        // Clean up the TradingView widget when the component is destroyed
        if (this.tradingViewWidget) {
            this.tradingViewWidget.remove();
            this.tradingViewWidget = null;
        }
    }
    loadTradingViewWidget(symbol) {
        const script = document.createElement('script');
        script.type = 'text/javascript';
        script.src = 'https://s3.tradingview.com/external-embedding/embed-widget-technical-analysis.js';
        script.async = true;
        script.onload = () => this.createTradingViewWidget(symbol);
        document.head.appendChild(script);
    }
    createTradingViewWidget(symbol) {
        if (!this.tradingViewWidget) {
            // If the widget doesn't exist, create it
            this.tradingViewWidget = new window.TradingView.widget({
                "interval": "1m",
                "width": 425,
                "isTransparent": false,
                "height": 450,
                "symbol": symbol,
                "showIntervalTabs": true,
                "displayMode": "single",
                "locale": "fr",
                "colorTheme": "light"
            });
        }
        else {
            // If the widget exists, update the symbol
            this.tradingViewWidget.chart().setSymbol(symbol);
        }
    }
    onSymbolChange(event) {
        const selectedSymbol = event.target.value;
        // Remove the existing TradingView widget
        if (this.tradingViewWidget) {
            this.tradingViewWidget.remove();
            this.tradingViewWidget = null;
        }
        // Load a new TradingView widget with the selected symbol
        this.loadTradingViewWidget(selectedSymbol);
    }
}
