import * as i0 from "@angular/core";
export class PortfolioComponent {
    constructor(renderer) {
        this.renderer = renderer;
        this.symbols = [
            { "proName": "FX_IDC:EURUSD", "title": "EUR vers USD" },
            { "proName": "BITSTAMP:BTCUSD", "title": "Bitcoin" },
            { "proName": "BITSTAMP:ETHUSD", "title": "Ethereum" },
            { "description": "Gold", "proName": "OANDA:XAUUSD" },
            { "description": "OIL", "proName": "TVC:USOIL" },
            { "description": "Apple", "proName": "NASDAQ:AAPL" }
        ];
    }
    ngOnInit() {
        this.loadTradingViewScript();
    }
    loadTradingViewScript() {
        const script = this.renderer.createElement('script');
        script.type = 'text/javascript';
        script.src = 'https://s3.tradingview.com/external-embedding/embed-widget-ticker-tape.js';
        script.async = true;
        this.renderer.appendChild(document.body, script);
    }
    onSymbolChange() {
        // Update TradingView widget when the symbol changes
        if (this.selectedSymbol) {
            const widgetContainer = document.querySelector('.tradingview-widget-container');
            if (widgetContainer) {
                widgetContainer.innerHTML = ''; // Clear existing widget
                this.loadTradingViewScript(); // Load the script again with the new symbol
            }
        }
    }
}
