import { __decorate } from "tslib";
import { Component } from '@angular/core';
let Financialnews2Component = class Financialnews2Component {
    constructor(claimService) {
        this.claimService = claimService;
    }
    ngOnInit() {
        this.claimService.getTableData3().subscribe(data => {
            this.tableData = data;
        });
        this.loadTradingViewWidget();
    }
    loadTradingViewWidget() {
        const script = document.createElement('script');
        script.type = 'text/javascript';
        script.src = 'https://s3.tradingview.com/tv.js';
        script.async = true;
        script.onload = () => this.createTradingViewWidget("NASDAQ:MSFT"); // Default symbol
        document.head.appendChild(script);
    }
    createTradingViewWidget(symbol) {
        new window.TradingView.widget({
            "autosize": true,
            "symbol": symbol,
            "interval": "D",
            "timezone": "Etc/UTC",
            "theme": "dark",
            "style": "1",
            "locale": "fr",
            "enable_publishing": true,
            "allow_symbol_change": true,
            "container_id": "tradingview_c976b"
        });
    }
    onSymbolChange(event) {
        const selectedSymbol = event.target.value;
        const tradingViewWidget = window.TradingView.widget.instances[0]; // Assuming only one instance
        tradingViewWidget && tradingViewWidget.chart().setSymbol(selectedSymbol);
    }
};
Financialnews2Component = __decorate([
    Component({
        selector: 'app-financialnews2',
        templateUrl: './financialnews2.component.html',
        styleUrls: ['./financialnews2.component.css']
    })
], Financialnews2Component);
export { Financialnews2Component };
